import React, { useEffect, useState } from "react";
import { getEquipmentList } from "../api/mockAPI";

export default function EquipmentList() {
  const [items, setItems] = useState([]);

  useEffect(() => {
    setItems(getEquipmentList());
  }, []);

  return (
    <div className="card">
      <h2>Equipment List</h2>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Category</th>
            <th>Condition</th>
            <th>Qty</th>
            <th>Available</th>
          </tr>
        </thead>
        <tbody>
          {items.map((i) => (
            <tr key={i.id}>
              <td>{i.name}</td>
              <td>{i.category}</td>
              <td>{i.condition}</td>
              <td>{i.quantity}</td>
              <td>{i.availability ? "✅" : "❌"}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
