import React, { useEffect, useState } from "react";
import { getItems, getRequests, updateRequestStatus } from "../api/mockAPI";
import "../styles.css";

export default function LabAssistantDashboard({ user }) {
  const [items, setItems] = React.useState([]);
  const [requests, setRequests] = React.useState([]);
  const [newItem, setNewItem] = React.useState({ name: "", category: "", condition: "", quantity: "" });

  React.useEffect(() => {
    setItems(getItems());
    setRequests(getRequests());
  }, []);

  if (!user || user.role !== "LabAssistant") {
    return <div>Access Denied</div>;
  }

  function handleAction(id, status) {
    updateRequestStatus(id, status);
    setRequests(getRequests());
  }

  return (
    <div>
      <h2>Lab Assistant Dashboard</h2>
      <h3>Pending Requests</h3>
      {requests.filter((r) => r.status === "Pending").length === 0 && <p>No pending requests</p>}
      {requests.filter((r) => r.status === "Pending").map((r) => (
        <div key={r.id}>
          {r.itemName} ({r.qty}) requested by {r.requestedByName}
          <button onClick={() => handleAction(r.id, "Approved")}>Approve</button>
          <button onClick={() => handleAction(r.id, "Rejected")}>Reject</button>
        </div>
      ))}
      <h3>All Requests</h3>
      {requests.map((r) => (
        <div key={r.id}>
          {r.itemName} ({r.qty}) - {r.status} by {r.requestedByName}
          {r.status === "Approved" && <button onClick={() => handleAction(r.id, "Returned")}>Mark Returned</button>}
        </div>
      ))}
    </div>
  );
}
