import React, { useEffect, useState } from "react";
import { getAllRequests, updateRequestStatus } from "../api/mockAPI";

export default function LabAssistantDashboard({ user }) {
  const [requests, setRequests] = useState([]);

  useEffect(() => {
    setRequests(getAllRequests());
  }, []);

  const handleAction = (id, status) => {
    updateRequestStatus(id, status, user.name);
    setRequests(getAllRequests());
  };

  return (
    <div className="card">
      <h2>Lab Assistant Dashboard</h2>
      {requests.map((r) => (
        <div key={r.id} className="request-row">
          <strong>{r.itemName}</strong> - {r.status}
          <div className="muted">Requested by: {r.requestedByName}</div>
          {r.status === "Pending" && (
            <>
              <button onClick={() => handleAction(r.id, "Approved")}>Approve</button>
              <button onClick={() => handleAction(r.id, "Rejected")}>Reject</button>
            </>
          )}
          {r.status === "Approved" && (
            <button onClick={() => handleAction(r.id, "Returned")}>Mark Returned</button>
          )}
        </div>
      ))}
    </div>
  );
}
