import React, { useEffect, useState } from "react";
import { getEquipmentList, requestEquipment } from "../api/mockAPI";

export default function StudentDashboard({ user }) {
  const [equipment, setEquipment] = useState([]);
  const [msg, setMsg] = useState("");

  useEffect(() => {
    setEquipment(getEquipmentList());
  }, []);

  const handleRequest = (id) => {
    try {
      requestEquipment(id, user.userId);
      setMsg("Request submitted!");
    } catch (e) {
      setMsg(e.message);
    }
  };

  return (
    <div className="card">
      <h2>Request Equipment</h2>
      {msg && <p className="success">{msg}</p>}
      {equipment.map((eq) => (
        <div key={eq.id} className="request-row">
          <strong>{eq.name}</strong> - {eq.category} ({eq.condition})
          <button onClick={() => handleRequest(eq.id)}>Request</button>
        </div>
      ))}
    </div>
  );
}
