import React, { useEffect, useState } from "react";
import { getEquipmentList } from "../api/mockAPI";

export default function EquipmentList() {
  const [items, setItems] = useState([]);

  useEffect(() => {
    async function fetchData() {
      const data = await getEquipmentList();
      console.log(data);
      setItems(data);
    }
    fetchData();
  }, []);

  return (
    <div className="container my-4">
      <h2 className="mb-4 text-center">Equipment List</h2>
      <div className="row">
        {items.length === 0 && <p className="text-muted">No equipment available</p>}
        {items.map((i) => (
          <div key={i.id} className="col-md-4 mb-4">
            <div className="card h-100 shadow-sm">
              <img
                src={i.thumbnail || "https://via.placeholder.com/150"}
                className="card-img-top"
                alt={i.name}
                style={{ height: "150px", objectFit: "cover" }}
              />
              <div className="card-body d-flex flex-column">
                <h5 className="card-title">{i.name}</h5>
                <p className="card-text mb-1"><strong>Category:</strong> {i.category}</p>
                <p className="card-text mb-1"><strong>Condition:</strong> {i.condition}</p>
                <p className="card-text mb-1"><strong>Quantity:</strong> {i.quantity}</p>
                <p className="card-text">
                  <strong>Available:</strong> {i.availability ? "✅" : "❌"}
                </p>
                <button
                  className="btn btn-primary mt-auto"
                  disabled={!i.availability || i.quantity === 0}
                >
                  {i.availability && i.quantity > 0 ? "Request" : "Unavailable"}
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
