import React, { useEffect, useState } from "react";
import { getItems, addRequest, getRequests } from "../api/mockAPI";
import "../styles.css"

export default function Student({ user }) {
    const [items, setItems] = React.useState([]);
    const [requests, setRequests] = React.useState([]);
    const [newItem, setNewItem] = React.useState({ name: "", category: "", condition: "", quantity: "" });
  
    React.useEffect(() => {
      setItems(getItems());
      setRequests(getRequests());
    }, []);
  
    if (!user || user.role !== "Student") {
      return <div>Access Denied</div>;
    }

  function handleRequest(item) {
    addRequest({
      itemId: item.id,
      itemName: item.name,
      qty: 1,
      requestedByName: user.name,
      status: "Pending",
    });
    alert("Request submitted!");
  }

  return (
    <div>
      <h2>Student Dashboard</h2>
      <h3>Available Equipment</h3>
      {items.length === 0 && <p>No equipment</p>}
      {items.map((it) => (
        <div key={it.id}>
          {it.name} - {it.quantity} pcs <button onClick={() => handleRequest(it)}>Request</button>
        </div>
      ))}
    </div>
  );
}
