import React, { useState, useEffect } from "react";
import { getItems, addItem, deleteItem, getRequests } from "../api/mockAPI";
// import "./styles.css";

export default function AdminDashboard({ user }) {
    const [items, setItems] = React.useState([]);
    const [requests, setRequests] = React.useState([]);
    const [newItem, setNewItem] = React.useState({ name: "", category: "", condition: "", quantity: "" });
  
    React.useEffect(() => {
      setItems(getItems());
      setRequests(getRequests());
    }, []);
  
    if (!user || user.role !== "Admin") {
      return <div>Access Denied</div>;
    }
  function handleAddItem(e) {
    e.preventDefault();
    addItem({ ...newItem, quantity: parseInt(newItem.quantity) });
    setItems(getItems());
    setNewItem({ name: "", category: "", condition: "", quantity: "" });
  }

  function handleDelete(id) {
    deleteItem(id);
    setItems(getItems());
  }

  return (
    <div>
      <h2>Admin Dashboard</h2>
      <section>
        <h3>Add Equipment</h3>
        <form onSubmit={handleAddItem}>
          <input placeholder="Name" value={newItem.name} onChange={(e) => setNewItem({ ...newItem, name: e.target.value })} required />
          <input placeholder="Category" value={newItem.category} onChange={(e) => setNewItem({ ...newItem, category: e.target.value })} required />
          <input placeholder="Condition" value={newItem.condition} onChange={(e) => setNewItem({ ...newItem, condition: e.target.value })} required />
          <input type="number" placeholder="Quantity" value={newItem.quantity} onChange={(e) => setNewItem({ ...newItem, quantity: e.target.value })} required />
          <button type="submit">Add</button>
        </form>
      </section>

      <section>
        <h3>Equipment List</h3>
        {items.length === 0 && <p>No equipment</p>}
        {items.map((it) => (
          <div key={it.id}>
            {it.name} - {it.quantity} pcs <button onClick={() => handleDelete(it.id)}>Delete</button>
          </div>
        ))}
      </section>

      <section>
        <h3>Requests</h3>
        {requests.length === 0 ? <p>No requests yet</p> : requests.map((r) => (
          <div key={r.id}>
            {r.itemName} ({r.qty}) - {r.status}
          </div>
        ))}
      </section>
    </div>
  );
}
