import React, { useEffect, useState } from "react";
import { listRequests, updateRequestStatus } from "../api/mockAPI";

export default function LabAssistantDashboard() {
  const [requests, setRequests] = useState([]);

  useEffect(() => {
    setRequests(listRequests());
  }, []);

  return (
    <div>
      <h2>Lab Assistant Dashboard</h2>
      <ul>
        {requests.map(r => (
          <li key={r.id}>
            {r.equipmentName} - {r.status}
            <button onClick={() => { updateRequestStatus(r.id, "Returned"); setRequests(listRequests()); }}>Mark Returned</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
