import React, { useState, useEffect } from "react";
import { listEquipments, addEquipment, deleteEquipment, listRequests, updateRequestStatus } from "../api/mockAPI";

export default function AdminDashboard() {
  const [equipments, setEquipments] = useState([]);
  const [requests, setRequests] = useState([]);
  const [newItem, setNewItem] = useState({ name: "", category: "", condition: "", quantity: "" });

  useEffect(() => {
    setEquipments(listEquipments());
    setRequests(listRequests());
  }, []);

  const handleAdd = (e) => {
    e.preventDefault();
    addEquipment({ ...newItem, available: true });
    setEquipments(listEquipments());
    setNewItem({ name: "", category: "", condition: "", quantity: "" });
  };

  return (
    <div>
      <h2>Admin Dashboard</h2>

      <section>
        <h3>Add Equipment</h3>
        <form onSubmit={handleAdd}>
          <input placeholder="Name" value={newItem.name} onChange={(e) => setNewItem({ ...newItem, name: e.target.value })} />
          <input placeholder="Category" value={newItem.category} onChange={(e) => setNewItem({ ...newItem, category: e.target.value })} />
          <input placeholder="Condition" value={newItem.condition} onChange={(e) => setNewItem({ ...newItem, condition: e.target.value })} />
          <input placeholder="Quantity" value={newItem.quantity} onChange={(e) => setNewItem({ ...newItem, quantity: e.target.value })} />
          <button type="submit">Add</button>
        </form>
      </section>

      <section>
        <h3>Equipment List</h3>
        <ul>
          {equipments.map(e => (
            <li key={e.id}>
              {e.name} - {e.quantity} pcs <button onClick={() => { deleteEquipment(e.id); setEquipments(listEquipments()); }}>Delete</button>
            </li>
          ))}
        </ul>
      </section>

      <section>
        <h3>Requests</h3>
        <ul>
          {requests.map(r => (
            <li key={r.id}>
              {r.equipmentName} - {r.status}
              <button onClick={() => { updateRequestStatus(r.id, "Approved"); setRequests(listRequests()); }}>Approve</button>
              <button onClick={() => { updateRequestStatus(r.id, "Rejected"); setRequests(listRequests()); }}>Reject</button>
            </li>
          ))}
        </ul>
      </section>
    </div>
  );
}
