// src/api/mockApi.js
const STORAGE_KEYS = {
    ITEMS: "se_items",
    REQUESTS: "se_requests",
    USERS: "se_users",
    CURRENT_USER: "currentUser",
  };
  
  export function initMockData() {
    if (!localStorage.getItem(STORAGE_KEYS.ITEMS)) {
      const items = [
        { id: "i1", name: "Microscope", category: "Lab", condition: "Good", quantity: 5 },
        { id: "i2", name: "Football", category: "Sports", condition: "Fair", quantity: 10 },
      ];
      localStorage.setItem(STORAGE_KEYS.ITEMS, JSON.stringify(items));
    }
    if (!localStorage.getItem(STORAGE_KEYS.REQUESTS)) {
      localStorage.setItem(STORAGE_KEYS.REQUESTS, JSON.stringify([]));
    }
  }
  
  export function loginUser(email, password) {
    if (email.includes("admin"))
      return { token: "mock-admin", role: "Admin", name: "Admin User" };
    if (email.includes("lab"))
      return { token: "mock-lab", role: "LabAssistant", name: "Lab Assistant" };
    if (email.includes("student"))
      return { token: "mock-student", role: "Student", name: "Student User" };
    return null;
  }
  
  // Equipment handling
  export function getItems() {
    return JSON.parse(localStorage.getItem(STORAGE_KEYS.ITEMS) || "[]");
  }
  
  export function addItem(item) {
    const items = getItems();
    items.push({ ...item, id: `i${Date.now()}` });
    localStorage.setItem(STORAGE_KEYS.ITEMS, JSON.stringify(items));
  }
  
  export function deleteItem(id) {
    const items = getItems().filter((i) => i.id !== id);
    localStorage.setItem(STORAGE_KEYS.ITEMS, JSON.stringify(items));
  }
  
  // Requests
  export function getRequests() {
    return JSON.parse(localStorage.getItem(STORAGE_KEYS.REQUESTS) || "[]");
  }
  
  export function addRequest(req) {
    const requests = getRequests();
    requests.push({ ...req, id: `r${Date.now()}`, status: "Pending" });
    localStorage.setItem(STORAGE_KEYS.REQUESTS, JSON.stringify(requests));
  }
  
  export function updateRequestStatus(id, status) {
    const requests = getRequests().map((r) =>
      r.id === id ? { ...r, status } : r
    );
    localStorage.setItem(STORAGE_KEYS.REQUESTS, JSON.stringify(requests));
  }
