import React, { useEffect, useState } from "react";
import { getEquipmentList, addRequest } from "../api/mockAPI";

export default function EquipmentList() {
  const [items, setItems] = useState([]);

  useEffect(() => {
    getEquipmentList().then((data) => setItems(data));
  }, []);

  const handleRequest = (equipmentId) => {
    addRequest(equipmentId)
      .then((msg) => {
        alert(msg); // show confirmation
        // Refresh items to reflect quantity change
        return getEquipmentList();
      })
      .then((data) => setItems(data))
      .catch((err) => alert(err));
  };

  return (
    <div className="row row-cols-1 row-cols-md-3 g-3">
      {items.map((i) => (
        <div className="col" key={i.equipmentId}>
          <div className="card h-100">
            <div className="card-body d-flex flex-column">
              <h5 className="card-title">{i.name}</h5>
              <p className="card-text">Category: {i.category}</p>
              <p className="card-text">Condition: {i.condition}</p>
              <p className="card-text">Qty: {i.quantity}</p>
              <button
                className="btn btn-primary mt-auto"
                disabled={!i.availability || i.quantity === 0}
                onClick={() => handleRequest(i.equipmentId)}
              >
                {i.availability && i.quantity > 0 ? "Request" : "Unavailable"}
              </button>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}
