// Mock data for testing UI
let mockEquipments = [
    { id: 1, name: "Microscope", category: "Lab", condition: "Good", quantity: 5, available: true },
    { id: 2, name: "Football", category: "Sports", condition: "Fair", quantity: 10, available: true },
  ];
  
  let mockRequests = [];
  
  export function loginUser(email, password) {
    if (email.includes("admin")) return { token: "mock-admin", role: "Admin", name: "Admin User" };
    if (email.includes("lab")) return { token: "mock-lab", role: "LabAssistant", name: "Lab Assistant" };
    return { token: "mock-student", role: "Student", name: "Student User" };
  }
  
  
  // Equipment APIs
  export function listEquipments() {
    return mockEquipments;
  }
  
  export function addEquipment(item) {
    item.id = mockEquipments.length + 1;
    mockEquipments.push(item);
    return "Equipment added";
  }
  
  export function deleteEquipment(id) {
    mockEquipments = mockEquipments.filter(e => e.id !== id);
    return "Equipment deleted";
  }
  
  // Request APIs
  export function requestEquipment(equipmentId, requestedBy) {
    const req = {
      id: mockRequests.length + 1,
      equipmentId,
      requestedBy,
      status: "Pending",
    };
    mockRequests.push(req);
    return "Request submitted";
  }
  
  export function listRequests() {
    return mockRequests.map(r => {
      const equipment = mockEquipments.find(e => e.id === r.equipmentId);
      return { ...r, equipmentName: equipment?.name || "Unknown" };
    });
  }
  
  export function updateRequestStatus(requestId, status) {
    const req = mockRequests.find(r => r.id === requestId);
    if (req) req.status = status;
    return "Request updated";
  }
  
