import React, { useEffect, useState } from "react";
import { getEquipmentList, addEquipment, deleteEquipment } from "../api/mockAPI";


export default function AdminDashboard() {
  const [items, setItems] = useState([]);
  const [form, setForm] = useState({ name: "", category: "", condition: "", quantity: 1 });

  useEffect(() => {
    setItems(getEquipmentList());
  }, []);

  const handleAdd = () => {
    addEquipment({ ...form, availability: true });
    setItems(getEquipmentList());
  };

  const handleDelete = (id) => {
    deleteEquipment(id);
    setItems(getEquipmentList());
  };

  return (
    <div className="card">
      <h2>Admin - Manage Equipment</h2>
      <input placeholder="Name" onChange={(e) => setForm({ ...form, name: e.target.value })} />
      <input placeholder="Category" onChange={(e) => setForm({ ...form, category: e.target.value })} />
      <input placeholder="Condition" onChange={(e) => setForm({ ...form, condition: e.target.value })} />
      <input
        type="number"
        placeholder="Quantity"
        onChange={(e) => setForm({ ...form, quantity: e.target.value })}
      />
      <button onClick={handleAdd}>Add Equipment</button>

      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Qty</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {items.map((i) => (
            <tr key={i.id}>
              <td>{i.name}</td>
              <td>{i.quantity}</td>
              <td>
                <button onClick={() => handleDelete(i.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
