// src/api/mockApi.js

const STORAGE_KEYS = {
    ITEMS: 'se_items',
    REQUESTS: 'se_requests',
    USERS: 'se_users',
    CURRENT_USER: 'se_current_user',
  };
  
  // Initialize mock data
  export function initMockData() {
    if (!localStorage.getItem(STORAGE_KEYS.ITEMS)) {
      const items = [
        { id: 'i1', name: 'Microscope', category: 'Lab', condition: 'Good', qty: 5, available: true },
        { id: 'i2', name: 'Projector', category: 'Electronics', condition: 'Excellent', qty: 2, available: true },
        { id: 'i3', name: 'Football', category: 'Sports', condition: 'Fair', qty: 10, available: true },
      ];
      localStorage.setItem(STORAGE_KEYS.ITEMS, JSON.stringify(items));
    }
  
    if (!localStorage.getItem(STORAGE_KEYS.USERS)) {
      const users = [
        { id: 'u1', name: 'Admin User', email: 'admin@school.edu', password: 'admin', role: 'admin' },
        { id: 'u2', name: 'Lab Assistant', email: 'lab@school.edu', password: 'lab', role: 'lab' },
        { id: 'u3', name: 'Student User', email: 'student@school.edu', password: 'student', role: 'student' },
      ];
      localStorage.setItem(STORAGE_KEYS.USERS, JSON.stringify(users));
    }
  
    if (!localStorage.getItem(STORAGE_KEYS.REQUESTS)) {
      localStorage.setItem(STORAGE_KEYS.REQUESTS, JSON.stringify([]));
    }
  }
  
  // --- User functions ---
  export function loginUser(email, password) {
    const users = JSON.parse(localStorage.getItem(STORAGE_KEYS.USERS));
    return users.find(u => u.email === email && u.password === password);
  }
  
  export function setCurrentUser(user) {
    localStorage.setItem(STORAGE_KEYS.CURRENT_USER, JSON.stringify(user));
  }
  
  export function getCurrentUser() {
    return JSON.parse(localStorage.getItem(STORAGE_KEYS.CURRENT_USER));
  }
  
  // --- Items ---
  export function getItems() {
    return JSON.parse(localStorage.getItem(STORAGE_KEYS.ITEMS)) || [];
  }
  
  // --- Requests ---
  export function getRequests() {
    return JSON.parse(localStorage.getItem(STORAGE_KEYS.REQUESTS)) || [];
  }
  
  export function saveRequests(requests) {
    localStorage.setItem(STORAGE_KEYS.REQUESTS, JSON.stringify(requests));
  }
  
  export function addRequest(req) {
    const requests = getRequests();
    requests.push(req);
    saveRequests(requests);
  }
  
  export function updateRequestStatus(reqId, status) {
    const requests = getRequests().map(r => (r.id === reqId ? { ...r, status } : r));
    saveRequests(requests);
  }
